mapper.select:
SELECT

FROM
    catalog_table t
        JOIN catalog_database d ON t.database_id = d.id
WHERE
    d.catalog_name = #{catalog}
  AND d.`name` = #{database}
  AND t.kind = #{kind}
ORDER BY t.'name', t.create_time

SELECT COUNT(*)
FROM catalog_table t
         JOIN catalog_database d ON t.database_id = d.id
WHERE
    d.catalog_name = #{catalog}
  AND d.`name` = #{database}
  AND t.kind = #{kind}

SELECT

FROM catalog_table t
JOIN catalog_database d ON t.database_id = d.id
WHERE
d.catalog_name = #{catalog}
AND d.`name` = #{database}
AND t.kind = #{kind}
AND t.`name` = #{name}

mapper.delete:
DELETE
FROM catalog_table
WHERE id = (SELECT ct.id
            FROM (SELECT t.id
                  FROM catalog_table t
                           JOIN catalog_database d ON t.database_id = d.id
                  WHERE d.catalog_name = #{catalog}
                    AND d.`name` = #{database}
                    AND t.kind = #{kind}
                    AND t.`name` = #{name}) AS ct
                )
